import React from "react";
import { useState } from "react";
var converter = require("hex2dec");

const LoginForm = (props) => {
  const { sender, provider } = props;
  const [receiver, setReceiver] = useState("");
  const [amount, setAmount] = useState("");
  const [error, setError] = useState("");
  const [transaction, setTransaction] = useState({});

  //transfer function is called when the transfers button is clucked on the screen
  async function sendMessage(e) {
    e.preventDefault();
  }
  return (
    <div className="w-full max-w-xs mx-auto">
      <h1 className="text-center">Message form</h1>
      <form
        className="bg-cyan-200 shadow-md rounded px-8 pt-6 pb-8 mb-4"
        onSubmit={sendMessage}
      >
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2">
            Address
          </label>
          <input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            id="username"
            type="text"
            placeholder="address"
            required
            value={receiver}
            onChange={(event) => {
              setReceiver(event.target.value);
            }}
          />
        </div>
        <div className="mb-6">
          <label className="block text-gray-700 text-sm font-bold mb-2">
            Message
          </label>
          <input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
            id="value"
            type="text"
            placeholder="Message"
            value={amount}
            required
            onChange={(event) => {
              setAmount(event.target.value);
            }}
          />
        </div>
        <div className="flex items-center justify-between">
          <button
            className="bg-cyan-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline w-full"
            type="submit"
            onSubmit={() => {
              sendMessage();
            }}
          >
            Transfer
          </button>
        </div>
      </form>
      {!error ? (
        <div>
          {transaction ? (
            <div>
              <p>From:{transaction.from}</p>
              <p>to:{transaction.to}</p>
              <p>gasUsed:{transaction.gasUsed}</p>
              <p>hash:{transaction.hash}</p>
            </div>
          ) : (
            <p></p>
          )}
        </div>
      ) : (
        <p>{error}</p>
      )}
    </div>
  );
};

export default LoginForm;
