import logo from "./assets/MetaMask_Fox.svg.png";
import ConnectionButton from "./component/button";
import { useState } from "react";
function App() {
  const [data, setData] = useState();
  async function connectToMetamask() {
    try {
      provider = await detectEthereumProvider();
      accounts = await provider.send("eth_requestAccounts", []);
      console.log(accounts.result[0]);
    } catch (error) {
      //return <p>{error.message}</p>;
      console.log(error);
    }
  }
  return (
    <div className="flex items-center justify-center min-h-screen">
      <div>
        <img src={logo} className="w-52 h-52 border-2"></img>
        <button
          className="w-full bg-green-500 mt-2 mb-2 h-10"
          onClick={() => {
            connectToMetamask();
          }}
        >
          Connect
        </button>
      </div>
    </div>
  );
}
export default App;
