import React from "react";
import { useState } from "react";

const LoginForm = (props) => {
  const { sender, provider } = props;
  const [receiver, setReceiver] = useState("");
  const [amount, setAmount] = useState("");

  function convertTowei(value) {
    console.log()
    let wei = 1000000000000000000 * value;
    return wei;
  }
  async function tranfer() {
    try {
      let calculatedWei = convertTowei(parseInt(amount));
      console.log(calculatedWei);
      console.log(calculatedWei.toString(16));
      const transaction = await provider.request({
        method: "eth_sendTransaction",
        params: [
          {
            from: sender,
            to: receiver,
            value: calculatedWei.toString(16),
          },
        ],
      });
      console.log(transaction);
    } catch (error) {
      console.log(error);
    }
  }
  return (
    <div className="w-full max-w-xs">
      <h1 className="text-center">Transfer form</h1>
      <form className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2">
            Address
          </label>
          <input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            id="username"
            type="text"
            placeholder="address"
            value={receiver}
            onChange={(event) => {
              setReceiver(event.target.value);
            }}
          />
        </div>
        <div className="mb-6">
          <label className="block text-gray-700 text-sm font-bold mb-2">
            Ether
          </label>
          <input
            className="shadow appearance-none border border-red-500 rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
            id="value"
            type="text"
            placeholder="Amount"
            value={amount}
            onChange={(event) => {
              setAmount(event.target.value);
            }}
          />
        </div>
        <div className="flex items-center justify-between">
          <button
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            type="button"
            onClick={() => {
              tranfer();
            }}
          >
            Transfer
          </button>
        </div>
      </form>
    </div>
  );
};

export default LoginForm;
