import React from "react";
import { useState } from "react";
var converter = require("hex2dec");

const LoginForm = (props) => {
  const { sender, provider } = props;
  const [receiver, setReceiver] = useState("");
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");
  const [transaction, setTransaction] = useState();
  const [signForm, setSignform] = useState(true);
  const [verifyForm, setVerifyform] = useState(flase);

  //transfer function is called when the transfers button is clucked on the screen
  async function sendMessage(e) {
    e.preventDefault();
  }
  return (
    <div className="w-full max-w-2xl mx-auto">
      <h1 className="text-center">Message form</h1>

      <form
        className="bg-cyan-200 shadow-md rounded px-8 pt-6 pb-8 mb-4 w-[500px]"
        onSubmit={sendMessage}
      >
        <div className="mb-6">
          <div className="flex justify-between">
            <button className="bg-cyan-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline w-full mb-5 mr-2">
              sign
            </button>
            <button className="bg-cyan-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline w-full mb-5 ml-2 ">
              verify
            </button>
          </div>
          <textarea
            rows={10}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
            id="value"
            type="text"
            placeholder="Message"
            value={message}
            required
            onChange={(event) => {
              setMessage(event.target.value);
            }}
          />
        </div>
        <div className="flex items-center justify-between">
          <button
            className="bg-cyan-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline w-full"
            type="submit"
            onSubmit={() => {
              sendMessage();
            }}
          >
            Sign Message
          </button>
        </div>
      </form>
      {!error ? (
        <div>
          {transaction ? (
            <div>
              <p>From:{transaction.from}</p>
              <p>to:{transaction.to}</p>
              <p>gasUsed:{transaction.gasUsed}</p>
              <p>hash:{transaction.hash}</p>
            </div>
          ) : (
            <p></p>
          )}
        </div>
      ) : (
        <p>{error}</p>
      )}
    </div>
  );
};

export default LoginForm;
