import React from "react";
import { ethers } from "ethers";
import { useState } from "react";
var converter = require("hex2dec");

const LoginForm = (props) => {
  const { sender, provider } = props;
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");
  const [verifymessage, setVerifyMessage] = useState("");
  const [transaction, setTransaction] = useState();
  const [signForm, setSignform] = useState(true);
  const [verifyForm, setVerifyform] = useState(false);
  //=================================================================
  const [signed, setSigned] = useState();
  const [verified, setVerified] = useState();

  //transfer function is called when the transfers button is clucked on the screen
  async function signMessage(e) {
    e.preventDefault();
    const signer = provider.getSigner();
    try {
      const signedMessage = await signer.signMessage(message);
      console.log(signedMessage);
      setSigned({
        address: sender,
        msg: message,
        sig: signedMessage,
      });
      setError(null);
    } catch (error) {
      setError(error.message);
    }
  }
  console.log(signed);
  async function verifyMessage(e) {
    e.preventDefault();
  }
  return (
    <div className="w-full max-w-2xl mx-auto">
      <h1 className="text-center">Message form</h1>
      {signForm ? (
        <form
          className="bg-cyan-200 shadow-md rounded px-8 pt-6 pb-8 mb-4 w-[500px]"
          onSubmit={signMessage}
        >
          <div className="mb-6">
            <div className="flex justify-between">
              <button
                className="bg-cyan-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline w-full mb-5 mr-2"
                onClick={() => {
                  setSignform(true);
                  setVerifyform(false);
                }}
              >
                sign
              </button>
              <button
                className="bg-cyan-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline w-full mb-5 ml-2"
                onClick={() => {
                  setVerifyform(true);
                  setSignform(false);
                  setSigned(null);
                }}
              >
                verify
              </button>
            </div>
            <textarea
              rows={10}
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
              id="value"
              type="text"
              placeholder="Message"
              required
              value={message}
              onChange={(event) => {
                setMessage(event.target.value);
              }}
            />
          </div>
          <div className="flex items-center justify-between">
            <button
              className="bg-cyan-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline w-full"
              type="submit"
              onSubmit={() => {
                signMessage();
              }}
            >
              Sign Message
            </button>
          </div>
        </form>
      ) : (
        <form
          className="bg-cyan-200 shadow-md rounded px-8 pt-6 pb-8 mb-4 w-[500px]"
          onSubmit={verifyMessage}
        >
          <div className="mb-6">
            <div className="flex justify-between">
              <button
                className="bg-cyan-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline w-full mb-5 mr-2"
                onClick={() => {
                  setSignform(true);
                  setVerifyform(false);
                }}
              >
                sign
              </button>
              <button
                className="bg-cyan-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline w-full mb-5 ml-2"
                onClick={() => {
                  setSignform(false);
                  setVerifyform(true);
                }}
              >
                verify
              </button>
            </div>
            <textarea
              rows={10}
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
              id="value"
              type="text"
              placeholder="{}"
              required
              value={verifyMessage}
              onChange={(event) => {
                setVerifyMessage(event.target.value);
              }}
            />
          </div>
          <div className="flex items-center justify-between">
            <button
              className="bg-cyan-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline w-full"
              type="submit"
              onSubmit={() => {
                verifyMessage();
              }}
            >
              verify
            </button>
          </div>
        </form>
      )}
      {!error ? (
        <div>
          {!signed ? (
            <div></div>
          ) : (
            <div>
              <p>address: {signed.address}</p>
              <p>msg: {signed.msg}</p>
              <p>sig: {signed.sig}</p>
            </div>
          )}
        </div>
      ) : (
        <p>{error}</p>
      )}
    </div>
  );
};

export default LoginForm;

